import profileImage from '../assets/profile.jpg';
export default function Home() {
  // Correct image path - assumes your image is in the src/assets folder
//   const profileImage = require('../assets/Datti.jpg').default;
// Option 2: Using import


  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-50 to-white">
      <main className="container mx-auto px-4 py-12">
        {/* Profile Section */}
        <div className="flex flex-col lg:flex-row items-center gap-12">
          {/* Image Container - Fixed sizing and border */}
          <div className="w-full lg:w-1/3 flex justify-center">
            <div className="relative group">
              <img 
                src={profileImage} 
                alt="Nuhu Muhammad Datti" 
                className="w-64 h-64 lg:w-80 lg:h-80 object-cover rounded-full border-4 border-gray-300 shadow-lg transition-transform duration-300 group-hover:scale-105"
                onError={(e) => {
                  e.target.onerror = null; 
                  e.target.src = "https://via.placeholder.com/500";
                }}
              />
              <div className="absolute -bottom-4 -right-4 bg-gray-800 text-white px-4 py-1 rounded-full text-sm font-medium shadow-md">
                EST. 2023
              </div>
            </div>
          </div>

          {/* Content Section */}
          <div className="w-full lg:w-2/3 space-y-6">
            <h1 className="text-4xl font-bold text-gray-900">Hi, I'm Nuhu Muhammad Datti</h1>
            <h2 className="text-2xl text-gray-600">Founder of 5IRE | Blockchain Architect</h2>
            
            <div className="space-y-4 text-gray-700">
              <p className="text-lg leading-relaxed">
                From crafting my first learning academy in Primary 3 to architecting blockchain-immortalized educational ecosystems, I don't wait for the futureâ€”I engineer it.
              </p>
              
              <div className="p-4 bg-gray-100 rounded-lg border-l-4 border-gray-400">
                <p>
                  While others chase certifications, I chase <span className="font-semibold text-gray-900">significance</span>. 
                  A diploma failure turned founder. A SIWES student turned digital emperor.
                </p>
              </div>

              <ul className="space-y-2 pl-5 list-disc text-gray-700">
                <li>Built EverLearn to turn games into greatness</li>
                <li>Transformed education with ULA's digital gold</li>
                <li>Pioneered AI in public service through PSI</li>
              </ul>
            </div>
          </div>
        </div>

        {/* Legacy Quote */}
        <div className="mt-16 text-center border-t border-gray-200 pt-8">
          <blockquote className="text-xl italic text-gray-700">
            "From dust I began, to eternity I echo."
          </blockquote>
          <p className="mt-2 text-gray-600">â€” Legacy Code Line #1, 5IRE Protocol</p>
        </div>
      </main>
    </div>
  )
}
